#!/bin/bash
# ==============================================================
# Tomas Bruna
#
# Interface for plotting combined ROC curves generated by
# combinedROC.sh
#
# ==============================================================

if [ "$#" -eq 0 ]; then
    x1=0
    x2=100
    y1=0
    y2=100
elif [ "$#" -lt 4 ]; then
    echo "Usage: $0 [xmin xmax ymin ymax]"
    exit
else
    x1=$1
    x2=$2
    y1=$3
    y2=$4
fi

gnuplot -e "x1='$x1';x2='$x2';y1='$y1';y2='$y2';" $(dirname $0)/plotCombinedROC.gp

echo "#!/usr/bin/env bash" > curve.sh
echo "$0 $x1 $x2 $y1 $y2" >> curve.sh

convert -transparent white -density 600 curve.pdf -quality 100 curve.png
